import Head from 'next/head'
import styles from '../styles/Home.module.css'
import DatePicker from "react-datepicker";
import React from 'react'

export default function Home() {

    const [projectName ,setProjectName] = React.useState("");
    const [startDate ,setStartDate] = React.useState(new Date());
    const [listLength ,setListLength] = React.useState();

    const handleOnChange = function (setValue){
        return function (e){
            setValue(e.target.value)
        }
    }

    const intervals = [1,1,2,4,7];

    const workDays = [];
    let workday = 0
    for (let value of intervals){
        workDays.push(day+ value)
    }

    let day = 0;

    let finalDay = listLength + intervals.reduce(
        (accumulator, currentValue) => accumulator + currentValue
    );

    let ibinHouseArray = [];
    for (let j=0;j<finalDay;j++){
        let todayPlan = [];
        for (let i =0;i<listLength;i++){
            let todayNeedWork =  workDays.includes(i - j)
            if (todayNeedWork){
                todayPlan[i] = true;
            } else {
                todayPlan[i] = true;
            }
        }

        ibinHouseArray.push(todayPlan);
    }
    console.log(ibinHouseArray);

    return (
        <div className={styles.container}>
        <Head>
            <title>艾賓浩思</title>
            <link
            rel="stylesheet"
            href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css"
            integrity="sha384-JcKb8q3iqJ61gNV9KGb8thSsNjpSL0n8PARn9HuZOnIxN0hoP+VmmDGMN5t9UJ0Z"
            crossOrigin="anonymous"
            />
        </Head>

        <main className={styles.main}>
            <h1>艾賓浩思計畫表</h1>
            <form >
                <div className="form-row">
                    <div className="form-group col-md-6">
                        <label htmlFor="projectName">專案名稱</label>
                        <input
                            type="text"
                            className="form-control"
                            value={projectName}
                            onChange={handleOnChange(setProjectName)}
                            id="projectName"
                        />
                    </div>
                    <div className="form-group col-md-6">
                        <label htmlFor="startDate">開始日期</label>
                        <DatePicker
                            dateFormat="yyyy/MM/dd"
                            id="startDate"
                            className={"form-control"}
                            selected={startDate}
                            onChange={date => setStartDate(date)}
                        />
                    </div>
                </div>
                <div className="form-row">
                    <div className="form-group col-md-6">
                        <label htmlFor="listLength">List總數</label>
                        <input
                            type="number"
                            className="form-control"
                            value={listLength}
                            onChange={handleOnChange(setListLength)}
                            id="listLength"
                        />
                    </div>
                </div>
            </form>



            <table className={"table"}>
                <tr>
                    <td>日期/次數</td>


                </tr>
            </table>
        </main>

        <footer className={styles.footer}>

        </footer>
    </div>
  )
}
